mapper.select:
select 

from stock_detail
where warehouse_id = #{warehouseId,jdbcType=INTEGER}
  and goods_id = #{goodsId,jdbcType=INTEGER}

mapper.delete:
delete from stock_detail
where warehouse_id = #{warehouseId,jdbcType=INTEGER}
  and goods_id = #{goodsId,jdbcType=INTEGER}

mapper.insert:
insert into stock_detail (warehouse_id, goods_id, goods_num
  )
values (#{warehouseId,jdbcType=INTEGER}, #{goodsId,jdbcType=INTEGER}, #{goodsNum,jdbcType=INTEGER}
  )

mapper.update:
update stock_detail

  
    goods_num = #{goodsNum,jdbcType=INTEGER},
  

where warehouse_id = #{warehouseId,jdbcType=INTEGER}
  and goods_id = #{goodsId,jdbcType=INTEGER}

update stock_detail
set goods_num = #{goodsNum,jdbcType=INTEGER}
where warehouse_id = #{warehouseId,jdbcType=INTEGER}
  and goods_id = #{goodsId,jdbcType=INTEGER}
